import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
//import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import "../App.css";

const styles = {
    root: {
      height: 300
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 18,
      alignContent: "center",
    },
    pos: {
      marginBottom: 12,
    },
  };


  class PizzaCard extends Component {

    render() {
      return(

        <Card style={styles.root} variant="outlined">
        <CardContent>
          <Typography component='h5' style={styles.title} color="textPrimary" >
            {this.props.pizza.nom}
          </Typography>
          <Typography>
            Ingredients : 
            <ul>
              {this.props.pizza.ingredients.map((ingredient) => (<li key={ingredient.id}>{ingredient.nom}</li>))}
            </ul>
          </Typography>
        </CardContent>
        <CardActions>
          {/* <Button size="small" onClick={}>Done</Button>
          <Button size="small" onClick={}>Delete</Button> */}
        </CardActions>
        </Card>

    );
    }
  }

  export default PizzaCard
