import React, { Component, useState, useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';

import Header from './header';
import CardPizza from "./pizzacard";

function PizzaCards({pizzas, addPizza, cart, addToCart}){

  const pizza_const = [];

  useEffect(() => {
    fetch('http://localhost:3000/api/v1/readPizzas/')
      .then(res => (res.json()))
      .then(pizzas => {pizzas.forEach(pizza => {
          addPizza(pizzas.push(pizza));
        }); console.log(pizzas)}
      );
  });

  console.log(pizza_const);

  return (
    <div>
      <Grid container direction="row" alignItems="center" alignContent="center" justify="center" style={{ minHeight: '50vh', minWidth: '100vh' }}>
          {pizzas.map((pizza) =>
            (<Grid item
                  xs={4}
                  align="center">

              <div key={pizza._id}
                  style={{ margin:"5px", width:"300px"}}>

                <CardPizza
                    pizza={pizza}
                    cart={cart}
                    addToCart={addToCart}
              />
              </div>
            </Grid>
            ))}
      </Grid>
    </div>
  )
}

