import React, { useEffect , useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';

function Login({user_token, setUserToken}){

        const [username, setUsername] = useState("");
        const [password, setPassword] = useState("");
        const [show_modal_fail, setShowModalFail] = useState(false);


        const handleSubmitSignup = () => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/signup',
                data : {
                    username: username,
                    password: password,
                }
            }).then(response => {
                axios({
                    method: 'post',
                    url: 'http://localhost:3000/api/v1/signin',
                    data : {
                        username: username,
                        password: password,
                    }
                }).then(res => {
                    if (res.status === 200){
                        setUserToken(res.token);
                    }
                    else{
                        setShowModalFail(true);
                    }
                });
            });
        }

        const handleSubmitSignin = () => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/signin',
                data : {
                    username: username,
                    password: password,
                }
            }).then(res => {
                if (res.status === 200){
                    setUserToken(res.token);
                }
                else{
                    setShowModalFail(true);
                }
            });
        }

        return (
            <div>
                <Modal
                backdrop="static"
                >
                    <Modal.Header>
                    <Modal.Title>Connexion/Inscription</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group controlId="formBasiName">
                                <Form.Label>Nom d'utilisateur</Form.Label>
                                <Form.Control type="text" placeholder="Nom d'utilisateur" onChange={e => setUsername(e.target.value)}/>
                            </Form.Group>

                            <Form.Group controlId="formBasicSurname">
                                <Form.Label>Mot de passe</Form.Label>
                                <Form.Control type="password" placeholder="Mot de passe" onChange={e => setPassword(e.target.value)}/>
                            </Form.Group>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                    <Button size="small" variant="success" onClick={handleSubmitSignin}>Se connecter</Button>
                    <Button size="small" variant="success" onClick={handleSubmitSignup}>S'inscrire</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        )
}

export default Login;
