import React, { Component } from 'react';
import Grid from '@material-ui/core/Grid';
import Box from '@material-ui/core/Box';



import CardPizza from "./pizzacard";

class PizzaCards extends Component {
  constructor(){
    super();
    this.state = {
      pizzas : []
    }
  }

  componentDidMount(){
    fetch('http://localhost:3000/api/v1/readPizzas/')
    .then(res => (res.json()))
    .then(pizzas => this.setState({pizzas}));
  }


  render() {
    return (
        <Box justifyContent="center" display="flex">
          <Grid container spacing={2} direction="row" alignItems="center" justify="center" style={{ minWidth: '100vh', minHeight: '20vh' }}>
              {this.state.pizzas.map((pizza) => (<Grid item xs={3}> <div key={pizza.id} style={{ margin:"5px", width:"300px"}}><CardPizza pizza={pizza}/></div></Grid>))}
          </Grid>

            {/* <ul style={{justifyContent: "center", display: "flex", listStyle: "none"}}>
            this.state.pizzas.map((pizza) => (<li key={pizza.id} style={{ margin:"5px"}}><CardPizza pizza={pizza}/></li>) )}
            </ul> */}
        </Box>
    )
  }
}

export default PizzaCards
