import React, {useState} from 'react';
import {Redirect} from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import axios from "axios";

function Login({setUserToken}){

        const [email, setEmail] = useState("");
        const [password, setPassword] = useState("");
        const [show_modal_fail, setShowModalFail] = useState(false);
        const [redirect, setRedirect] = useState(false);


        const handleSubmitSignup = () => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/signup',
                data : {
                    email: email,
                    password: password,
                }
            }).then(response => {
                axios({
                    method: 'post',
                    url: 'http://localhost:3000/api/v1/signin',
                    data : {
                        email: email,
                        password: password,
                    }
                }).then(res => {
                    if (res.status === 200){
                        setUserToken(res.token);
                        setRedirect(true);
                    }
                    else{
                        setShowModalFail(true);
                    }
                });
            });
        };

        const handleSubmitSignin = () => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/signin',
                data : {
                    email: email,
                    password: password,
                }
            }).then(res => {
                if (res.status === 200){
                    setUserToken(res.data.token);
                    setRedirect(true);
                }
                else{
                    setShowModalFail(true);
                }
            });
        }

        const handleCloseModalFail = () => {setShowModalFail(false); setRedirect(true);};
        const handleShowModalFail = () => setShowModalFail(true);
        const handleCloseModal = () => setRedirect(true);

        return (
            <div>
                <Modal
                show={true}
                backdrop="static"
                >
                    <Modal.Header>
                    <Modal.Title>Connexion/Inscription</Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Form>
                            <Form.Group controlId="formBasiName">
                                <Form.Label>Email</Form.Label>
                                <Form.Control required type="text" placeholder="Email" onChange={e => setEmail(e.target.value)}/>
                                <Form.Control.Feedback type="invalid">
                                    Merci d'entrer email.
                                </Form.Control.Feedback>
                            </Form.Group>


                            <Form.Group controlId="formBasicSurname">
                                <Form.Label>Mot de passe</Form.Label>
                                <Form.Control required type="password" placeholder="Mot de passe" onChange={e => setPassword(e.target.value)}/>
                                <Form.Control.Feedback type="invalid">
                                    Merci d'entrer votre mot de passe.
                                </Form.Control.Feedback>
                            </Form.Group>
                        </Form>
                    </Modal.Body>
                    <Modal.Footer>
                    <Button size="small" type="submit" variant="success" onClick={handleSubmitSignin}>Se connecter</Button>
                    <Button size="small" type="submit" variant="success" onClick={handleSubmitSignup}>S'inscrire</Button>
                    <Button variant="secondary" onClick={handleCloseModal}>Fermer</Button>
                    </Modal.Footer>
                </Modal>

                {show_modal_fail && 
                    <div>
                        <Modal
                        show={handleShowModalFail}
                        onHide={handleCloseModalFail}
                        backdrop="static"
                        >
                            <Modal.Header closeButton>
                            </Modal.Header>
                            <Modal.Body>
                                L'authentification/inscription a échoué, veuillez réessayer.
                            </Modal.Body>
                            <Modal.Footer>
                            <Button variant="secondary" onClick={handleCloseModalFail}>Fermer</Button>
                            </Modal.Footer>
                        </Modal>
                    </div>
                }

                {redirect &&
                    <Redirect to="/" />
                }
            </div>
        )
}

export default Login;
