import React from 'react';
import { MemoryRouter as Router } from 'react-router';
import { Link } from 'react-router-dom'
import Button from 'react-bootstrap/Button';

import 'bootstrap/dist/css/bootstrap.min.css';



import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
// import Button from '@material-ui/core/Button';
import '../style/header.css';

function Header({cart}) {
  const useStyles = makeStyles((theme) => ({
      root: {
        flexGrow: 1,
      },
      menuButton: {
        marginRight: theme.spacing(2),
      },
      title: {
        flexGrow: 1,
      },

      appBar: {
        background: "linear-gradient(110deg, rgb(0, 146, 70) 33%, rgba(0, 0, 0, 0) 33%), linear-gradient(110deg, white 66%, rgb(206, 44, 56) 66%)",
      }

    }));

    const classes = useStyles();

    return (
      <div className={classes.root}>
        <AppBar position="static" className={classes.appBar}>
          <Toolbar>

            <Typography variant="h6" className={classes.title}>
            The Octopizza - Feeling Saucy Pizzeria
            </Typography>

              <Router>
                <div style={{marginRight:"10vh"}}>
                    <Button variant="outline-secondary" href="/menu">Menu</Button>
                    <Button variant="outline-secondary" href="/order">>Ma commande</Button>
                </div>
                  <Button variant="outline-secondary" href="/login">Se connecter</Button>
              </Router>
          </Toolbar>
        </AppBar>
      </div>
    );

}

export default Header;
