import React, { useEffect , useState} from 'react';
import axios from "axios";
import jwt_decode from "jwt-decode";
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

function Profile({user_token}){
    const [client_pizzas, setClientPizzas] = useState([]);
    const [email, setEmail] = useState("")

    useEffect(() => {
        if (user_token !== "") {
            const user = jwt_decode(user_token);
            setEmail(user.username);
            handleGetOrders(user.username);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);

    const handleGetOrders = (email) => {
        console.log(email);
        axios({
            method: 'post',
            url: 'http://localhost:3000/api/v1/readClient',
            data : {
                email: email,
            }
        }).then(response => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/readClientOrders',
                data : {
                    client: response.data[0]._id
                }
            }).then(res => {
                if (res.status === 200){
                    setClientPizzas(res.data[0].pizzas);
                }
            });
        });
    }

    console.log(client_pizzas);

    return (
        <div>
            <h1>Bienvenue {email}</h1>

            <Card>
                <CardContent>
                    <ul>
                        {client_pizzas.forEach(pizza => <li>{pizza.nom}</li>)}
                    </ul>
                </CardContent>
            </Card>
        </div>
    )
}

export default Profile;
