import React, { useEffect , useState} from 'react';
import axios from "axios";
import jwt_decode from "jwt-decode";
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';

function Profile({user_token}){
    const [client_pizzas, setClientPizzas] = useState([]);
    const [username, setUsername] = useState("")

    useEffect(() => {
        console.log(user_token);
        const user = jwt_decode(user_token);
        console.log(user);
        if (user_token !== "") {setUsername(jwt_decode(user_token).username);}
        handleGetOrders();
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);

    const handleGetOrders = () => {
        axios({
            method: 'post',
            url: 'http://localhost:3000/api/v1/readClient',
            data : {
                email: username,
            }
        }).then(response => {
            axios({
                method: 'post',
                url: 'http://localhost:3000/api/v1/readClientOrders',
                data : {
                    client: response.data._id
                }
            }).then(res => {
                if (res.status === 200){
                    setClientPizzas(res.data.pizzas);
                }
            });
        });
    }

    console.log(client_pizzas);

    return (
        <div>
            <h1>Bienvenue {username}</h1>

            <Card>
                <CardContent></CardContent>
            </Card>
        </div>
    )
}

export default Profile;
