            <CardActions style={styles.order}>
            <Button size="small" variant="contained" color="secondary" onClick={handleSubmit}>Commander</Button>
            <Button variant="outlined" color="primary">Personnaliser</Button>
            </CardActions>
            </Card>
        </div>
    )
}

export default Order;
