    // const url = "http://localhost:3000/api/v1/readPizzas/";
    // HTTP.open("GET", url);
    // HTTP.send();

    // HTTP.onreadystatechange=(e)=>{
    //   const res = JSON.parse(HTTP.responseText);
    //   addPizzas(res.map((item) => {
    //     return [...pizzas, {nom:item.nom, pate:item.pate, base:item.base, prix:item.prix, calzone:item.calzone, ingredients:item.ingredients, id:item._id}];
    //   }));
    // }

    fetch('http://localhost:3000/api/v1/readPizzas/')
    .then(res => (res.json()))
    .then(pizzas => addPizzas(pizzas.map((item) => {
            // pizzas.push({nom:item.nom, pate:item.pate, base:item.base, prix:item.prix, calzone:item.calzone, ingredients:item.ingredients, id:item._id});
            // return pizzas;
           return [...pizzas, {nom:item.nom, pate:item.pate, base:item.base, prix:item.prix, calzone:item.calzone, ingredients:item.ingredients, id:item._id}];
         }))
    );
  }

  if (pizzas.length === 0){
    getPizzas();
  }

  // pizzas.map((value, index) => console.log(value, index))
//   pizzas.map((value, index) => {
//     console.log(value);
// })

console.log(pizzas);

  return (
      <div>
          <h2>Pizzas</h2>
          <ul>
          {pizzas.map((value, index) => {
            return <CardPizza key={index} pizza={value[0]} pizzas={pizzas} addPizzas={addPizzas}/>
           })}
        </ul>

      </div>
  )
}
