//import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import "../App.css";

const useStyles = makeStyles({
    root: {
        width: 100,
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 18,
    },
    pos: {
      marginBottom: 12,
    },
  });

  class PizzaIngredients{
    constructor(){
      this.ingredients = [];
    }

    addIngredient(ingredient){
      this.ingredients.push(ingredient);
    }

    getIngredients(){return this.ingredients;}
  }


export default function CardPizza({pizza, pizzas, addPizzas}) {

  const pizza_ingredients = new PizzaIngredients();

  const pushIngredients = (ingredient_id) => {
    fetch('http://localhost:3000/api/v1/readIngredient/'+ingredient_id)
    .then(res => (res.json()))
    .then(ingredient => pizza_ingredients.addIngredient(ingredient.nom));
  }

  const getPizzaIngredients = () => {
    pizza.ingredients.map(ingredient => (pushIngredients(ingredient)))
  }

  if (pizza_ingredients.getIngredients().length === 0){
