import React, { useEffect } from 'react';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';


import axios from "axios";
import { Link } from 'react-router-dom'

import CardPizza from "./pizzacard";


function PizzaCards({pizzas, addPizza, cart, addToCart}){

  const fetchingData = () => {
    axios
      .get("http://localhost:3000/api/v1/readPizzas/")
      .then(response => {
         addPizza(response.data);
       }) // you have array in your response.data so add your data here
      .catch(err => console.log(err));
  };

  useEffect(() => {
    fetchingData();
     // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);


  return (
    <div>
      <Grid container xs={8} direction="row" alignItems="center" alignContent="center" justify="center" style={{ minHeight: '50vh', minWidth: '100vh' }}>
          {pizzas.map((pizza) =>
            (<Grid item
                  xs={4}
                  align="center">

              <div key={pizza._id}
                  style={{ margin:"5px", width:"300px"}}>

                <CardPizza
                    pizza={pizza}
                    cart={cart}
                    addToCart={addToCart}
              />
              </div>
            </Grid>
            ))}

        <Grid item xs={4} align="center" style={{marginTop: '10vh'}}>
          <Card variant="outlined">
            <CardContent >
              <Typography>
                Commande :
            {cart.map(c => {console.log(cart); return <p>{c}</p>})
                }
              </Typography>
            </CardContent>
            <CardActions >
              {/* <Button size="small" variant="contained" color="secondary" onClick={handleSubmit}>Commander</Button> */}
            </CardActions>
          </Card>
        </Grid>

        <Grid item xs={12} align="center" style={{marginTop: '10vh'}}>
          <Button variant="contained" component={Link} to={{pathname: "/order", state: {cart: {cart} }}} >Finaliser la commande</Button>
        </Grid>

      </Grid>
    </div>
  )
}

