import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
//import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import "../App.css";

const useStyles = makeStyles({
    root: {
        width: 100,
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 18,
    },
    pos: {
      marginBottom: 12,
    },
  });


  class PizzaCard extends Component {

    render() {
      console.log(this.props.pizza);
      return(

        <Card className={useStyles.root} variant="outlined">
        <CardContent>
          <Typography className={useStyles.title} color="textSecondary" gutterBottom>
            {this.props.pizza.nom}
          </Typography>
          <Typography>
            Ingredients : 
            <ul style={{justifyContent: "space-arround", display: "flex", alignItems: "center"}}>
              {this.props.pizza.ingredients.map((ingredient) =>
                <li>{ingredient.nom}</li>)
              }
            </ul>
          </Typography>
        </CardContent>
        <CardActions>
          {/* <Button size="small" onClick={}>Done</Button>
          <Button size="small" onClick={}>Delete</Button> */}
        </CardActions>
        </Card>

    );
    }
  }

  export default PizzaCard
