import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import {useLocation} from 'react-router-dom';


function Order(){

    const pizzas = useLocation().state.cart.cart;

    const prixPizzas = () => {let prix=0; pizzas.forEach(pizza => {prix += pizza.prix;}); return prix;}

    return (
        <div style={{width: "50%", alignItems: "center"}}>
            <h1>Finalisation de votre commande</h1>
            <Card  variant="outlined">
            <CardContent>
                <h3>Votre commande :</h3>
                <h4>{pizzas.length} pizzas</h4>
                <ul>
                    {pizzas.map((pizza) => 
                        <li>{pizza.nom}</li>
                    )}
                </ul>
            <Typography>
                Prix : {prixPizzas()} â‚¬
            </Typography>
            </CardContent>
            <CardActions >
                <Button size="small" variant="contained" color="secondary" >Valider</Button>
            </CardActions>
            </Card>
        </div>
    )
}

export default Order;
