import React from 'react';

import CardPizza from "./cardPizza";


export default function Cards({pizzas, addPizzas}) {

  const getIngredient = (id) => {
    const HTTP = new XMLHttpRequest();
    const url = "http://localhost:3000/api/v1/readIngredient/"+id
    HTTP.open("GET", url);
    HTTP.send();

    HTTP.onreadystatechange=(e)=>{
      const res = JSON.parse(HTTP.responseText);
      return res;
    }
  }

  const getPizzas = () => {
    const HTTP = new XMLHttpRequest();

    const url = "http://localhost:3000/api/v1/readPizzas/";
    HTTP.open("GET", url);
    HTTP.send();

    HTTP.onreadystatechange=(e)=>{
      const res = JSON.parse(HTTP.responseText);
      addPizzas(res.map((item) => {
        item.ingredients.map((ing, index) => {
          const ingredient = getIngredient(ing);
          console.log(ingredient);
          item.ingredients[index] = ingredient;
        })

        console.log(item.ingredients)

        return [...pizzas, {nom:item.nom, pate:item.pate, base:item.base, prix:item.prix, calzone:item.calzone, ingredients:item.ingredients, id:item._id}];
      }));
    }
  }

  if (pizzas.length === 0)
    getPizzas();

  return (
      <div>
          <h2>Pizzas</h2>
          <ul>
          {pizzas.map((value, index) => {
            return <CardPizza key={index} pizza={value[0]} pizzas={pizzas} addPizzas={addPizzas}/>
           })}
        </ul>

      </div>
  )
}
