import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';

import "../App.css";

const useStyles = makeStyles({
    root: {
        width: 100,
      minWidth: 275,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 18,
    },
    pos: {
      marginBottom: 12,
    },
  });

export default function CardPizza({todo, todos, setTodos}) {

    const classes = useStyles();

    const getData = () => {
      const HTTP = new XMLHttpRequest();

      const url = "http://localhost:3000/api/v1/readPizzas/";
      HTTP.open("GET", url);
      HTTP.send();

      console.log(HTTP.responseText);

      return HTTP.response;
    }

    const handleDone = () => {

        // ... submit to API or something
        setTodos(todos.map((item) => {
            if (item.id === todo.id) {
                return { 
                    ...item, done : !item.done
                }
            }

            return item;
        }));

    };

    const handleDelete = () => {

        // ... submit to API or something
        setTodos(todos.filter((el) => el.id !== todo.id));

    };

    return(

        <Card className={classes.root} variant="outlined">
        <CardContent>
          <Typography className={classes.title} color="textSecondary" gutterBottom>
            Test
          </Typography>
          <Typography variant="body2" component="p">
            {getData()}
          </Typography>
        </CardContent>
        <CardActions>
          <Button size="small" onClick={handleDone}>Done</Button>
          <Button size="small" onClick={handleDelete}>Delete</Button>
        </CardActions>
        </Card>

        // <div>
        //     <span className={`"title" ${todo.done ? "completed" : ''}`}> {todo.title} </span>    
        // </div>

    );
}
