import React, {useState} from 'react';
import Header from './components/header';
import Todo from './components/todo';
import List from './components/list';
import CardPizza from './components/card'


const initialFormData = Object.freeze({
  title: "",
  description: "",
  done: false,
  id : Math.ceil(Math.random() * 10000)
});

function App() {

  const [formData, updateFormData] = useState(initialFormData);

  const [pizzas, addPizzas] = useState([]);

  return (
    <div className="App">
      <Header/>
      {/* <Todo todos={todos} addTodos={addTodos} formData={formData} updateFormData={updateFormData}/> */}
      {/* <List todos={todos} setTodos={addTodos}/> */}
      <CardPizza pizzas={pizzas} setPizzas={addPizzas} />
    </div>
  );
}

export default App;
