import React from 'react';
import Header from './components/header';
import PizzaCards from './components/pizzacards'

import { BrowserRouter, Switch, Route } from "react-router-dom";

import Order from "./pages/Order"
//import PersoPizza from "./pages/PersoPizza"
//import Register from "./pages/Register"
import Login from "./pages/Login"
// import Account from "./pages/Account"
import Home from "./pages/Home"
import Menu from "./pages/Menu"

class App extends React.Component {
  state = {
    pizzas : []
  }

  render() {
    return (
      <div className="App">
        <Header />
            <BrowserRouter>
              <Switch>
              <div style={{marginRight:"10vh"}}>
                  <Route exact path="/">
                    <Home />
                  </Route>

                  <Route exact path="/menu">
                    <Menu />
                    {/* <Button color="inherit">Menu</Button> */}
                  </Route>

                  <Route exact path="/order">
                    <Order />
                    {/* <Button color="inherit">Commander</Button> */}
                  </Route>
                </div>
                <Route exact path="/login">
                  <Login />
                  {/* <Button variant="outlined" color="inherit">Se connecter</Button> */}
                </Route>

              </Switch>
            </BrowserRouter>        {/* <PizzaCards /> */}
      </div>
    );
  }
}


// function App() {

