import React from 'react';
import { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';


import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import {useLocation} from 'react-router-dom';


function Order(){
    const styles = {
        root: {
            width: "30%",
            marginLeft: "35%"
        },
        bullet: {
          display: 'inline-block',
          margin: '0 2px',
          transform: 'scale(0.8)',
        },
        title: {
          alignItems: "center",
        },
        card_content: {
          display: "block",
          textAlign: "center"
        },

        order: {
          display: "block",
          textAlign: "center",
        }
      };

    const pizzas = useLocation().state.cart.cart;

    const prixPizzas = () => {let prix=0; pizzas.forEach(pizza => {prix += pizza.prix;}); return prix;}

    const [formStatus, setShowForm] = useState(false);

    const showForm = () => {
        setShowForm(!formStatus);
    }

    const [showModal, setShowModal] = useState(false);

    const handleCloseModal = () => setShowModal(false);
    const handleShowModal = () => setShowModal(true);

    return (
        <div>
            <h1 style={{background: "transparent"}}>Finalisation de votre commande</h1>
            <Card style={styles.root} variant="outlined">
                <CardContent style={styles.card_content}>
                    <h3>Votre commande :</h3>
                    <h4>{pizzas.length} pizza(s)</h4>
                    <ul>
                        {pizzas.map((pizza) => 
                            <li>{pizza.nom}</li>
                        )}
                    </ul>
                    <Typography>
                        Prix : {prixPizzas()} €
                    </Typography>
                </CardContent>
            <CardActions style={styles.order}>
                <Button size="small" variant="success" onClick={showForm} >Valider</Button>
            </CardActions>
            </Card>

            {formStatus && 
            <div>
                      <Modal
                        show={handleShowModal}
                        onHide={handleCloseModal}
                        backdrop="static"
                        >
                        <Modal.Header closeButton>
                        <Modal.Title>Validation de votre commande</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <form id= "add-app">

                                <label>Application Name : </label>
                                <input type="text" />

                                <label> id : </label>
                                <input type="text" />

                                <label>Server details : </label>
                                <input />

                                <button>Create</button>
                            </form>
                        </Modal.Body>
                        <Modal.Footer>
                        <Button variant="secondary" onClick={handleCloseModal}>
                            Close
                        </Button>
                        </Modal.Footer>
                    </Modal>
            </div>
            }
        </div>
    )
}

export default Order;
