import React, { Component } from 'react';

import CardPizza from "./pizzacard";

class PizzaCards extends Component {
  constructor(){
    super();
    this.state = {
      pizzas : []
    }
  }

  componentDidMount(){
    fetch('http://localhost:3000/api/v1/readPizzas/')
    .then(res => (res.json()))
    .then(pizzas => this.setState({pizzas}));
  }


  render() {
    return (
        <div>
            <h2>Pizzas</h2>
            <ul style={{justifyContent: "space-arround", marginRight:"5px", display: "flex", alignItems: "center"}}>
            {this.state.pizzas.map((pizza) => (<li key={pizza.id} style={{listStyle: "none"}}><CardPizza pizza={pizza}/></li>) )}
            </ul>
        </div>
    )
  }
}

export default PizzaCards
