import React, {useState} from 'react';
import {Link, Redirect} from "react-router-dom";
import Button from 'react-bootstrap/Button';
// import Button from '@material-ui/core/Button';


import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import '../style/header.css';

function Header({user_token, setUserToken}) {
  const [redirect, setRedirect] = useState(false);

  const useStyles = makeStyles((theme) => ({
      root: {
        flexGrow: 1,
      },
      menuButton: {
        marginRight: theme.spacing(2),
      },
      title: {
        flexGrow: 1,
      },

      appBar: {
        background: "linear-gradient(110deg, rgb(0, 146, 70) 33%, rgba(0, 0, 0, 0) 33%), linear-gradient(110deg, white 66%, rgb(206, 44, 56) 66%)",
      }

    }));

    const classes = useStyles();

    const handleSubmitSignout = () => {setUserToken(""); setRedirect(true)};

    return (
      <div className={classes.root}>
        <AppBar position="static" className={classes.appBar}>
          <Toolbar>

            <Typography variant="h6" className={classes.title}>
              <Link to="/" style={{ textDecoration: 'none', color: 'white' }}>
                The Octopizza - Feeling Saucy Pizzeria
              </Link>
            </Typography>
              <div style={{marginRight:"2vh"}}>
                  {/* <Button variant="outline-light" component={Link} to={{pathname: "/menu"}} style={{marginRight: "5px"}}>Menu</Button> */}
                  <Button variant="outline-light" as={Link} href="/menu" style={{marginRight: "5px"}}>Menu</Button>

                  <Button variant="outline-light" href="/order">Ma commande</Button>
              </div>
                {user_token === "" && 
                  <Button variant="outline-light" href="/login">Se connecter</Button>
                }

                {user_token !== "" &&
                  <div>
                    <Button variant="outline-light" href="/profile" style={{marginRight: "5px"}}>Mon compte</Button>
                    <Button variant="outline-light" onClick={handleSubmitSignout}>Se d√©connecter</Button>
                  </div>
                }
          </Toolbar>
        </AppBar>

        {redirect &&
          <Redirect to="/" />
        }

      </div>
    );

}

export default Header;
